import "@babel/polyfill";
import React from "react";
import { render } from "react-dom";
import { connect, Provider } from "react-redux";
import { onCameraMove, onSelectRoom } from "./actions";
import Building from "./components/Building";
import Compass from "./components/Compass";
import Orientation from "./components/Orientation";
import Path from "./components/Path";
import store from "./store";
import "./style.css";
import { Dialog } from 'lucid-ui';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.cameraRef = React.createRef();
  }

  componentDidMount() {
    // this.cameraRef.current.addEventListener("componentchanged", evt => {
    //   if (evt.detail.name !== "position") {
    //     return;
    //   }
      // this.props.onCameraMove(evt.detail.target.getAttribute("position"));
    // });
  }

  render() {
    const {
      error,
      // coords,
      orientation,
      userPosition,
      initialHeading,
      initialLocation,
      distanceTraveled,
      targetRoomName,
    } = this.props;
    return (
      <div style={{ width: "100%", height: "100%" }}>
        error: {JSON.stringify(error)}<br />
        distanceTraveled: {distanceTraveled}
        <br />
        heading: {initialHeading}
        <br />
        userPosition: {JSON.stringify(userPosition)}
        <br />
        targetRoomName: {targetRoomName}
        <br />
        <Dialog
          isShown={!targetRoomName}
          Header="Select a room"
          size="small"
        >
          This `Dialog` has no footer!
        </Dialog>}

      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return state;
}

const mapDispatchToProps = {
  onCameraMove,
  onSelectRoom,
};

const ConnectedApp = connect(
  mapStateToProps,
  mapDispatchToProps
)(App);

render(
  <Provider store={store}>
    <ConnectedApp />
  </Provider>,
  document.getElementById("app")
);
